mapper.select:
SELECT SKIP #{skip} FIRST #{pageSize} p.code,p.id,p.in_time as inTime,p.name,p.fid as fid,j.name as jname,p.status,nvl(y.count,0) as count,f.name as fname,r.name as pname from plant_tel p left join (select equipment_code,count(equipment_code)as count from yj_police group by equipment_code) y on p.code=y.equipment_code inner join plant_factory f on p.fid=f.id inner join plant_tel_region r on p.region_id = r.id inner join jy j on p.pid = j.id
substr(r.code,3,2) = #{params.code}
and p.name like #{pattern} or j.name like #{pattern}

select count(*) from plant_tel p inner join plant_tel_region r on p.region_id = r.id inner join jy j on p.pid =
j.id


        1 = 1

            and p.status = #{params.status}


            and substr(r.code,0,2) = #{params.code}



            and (p.name like #{pattern} or j.name like #{pattern})
